import React from 'react';
import {ProductsContainer, ProductsHeader, ProductsGrid} from './HomeStyle';
import ProductCard from '../ProductCard/ProductCard';


export default function Home(props) {
  const filteredAndOrderedList = this.getFilteredAndOrderedList()
  return (
  <ProductsContainer>
    <ProductsHeader>
      <p>Quantidade de produtos: {filteredAndOrderedList.length}</p>
      <label>
        Ordenação:
        <select value={this.state.sort} onChange={this.onChangeSort}>
          <option value={'CRESCENTE'}>Crescente</option>
          <option value={'DECRESCENTE'}>Decrescente</option>
        </select>
      </label>
    </ProductsHeader>
    <ProductsGrid>
      {filteredAndOrderedList.map((product) => {
        return <ProductCard
          product={product}
          onAddProductToCart={this.props.onAddProductToCart}
        />
      })}
    </ProductsGrid>
  </ProductsContainer>
  )
}
