import React from 'react';
import { ShoppingCartContainer, CartListContainer } from './CartStyle';
import Items from '../Items/Items';

export default function CartSidebar(props) {
  const { cart, isOpen, onRemoveProductFromCart, onClose } = props;

  const sortedCart = cart.slice().sort((a, b) => {
    if (a.name < b.name) return -1;
    if (a.name > b.name) return 1;
    return 0;
  });

  const getTotalValue = () => {
    let totalValue = 0;

    for (let product of sortedCart) {
      if (product.value && product.quantity) {
        totalValue += product.value * product.quantity;
      }
    }

    return totalValue;
  };

  const formattedTotal = getTotalValue().toLocaleString('pt-BR', {
    style: 'currency',
    currency: 'BRL',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });

  return (
    <ShoppingCartContainer isOpen={isOpen}>
      <h3>Carrinho:</h3>
      <button onClick={onClose}>Fechar</button>
      <CartListContainer>
        {sortedCart.map((product) => (
          <Items
            key={product.id}
            cartItem={product}
            onRemoveProductFromCart={onRemoveProductFromCart}
          />
        ))}
      </CartListContainer>
      <p>Total: {formattedTotal}</p>
    </ShoppingCartContainer>
  );
}
