import React from 'react'
import { Items } from '../Items/Items'
import { ShoppingCartContainer, CartListContainer } from './CartStyle'

export default function Cart() {
  getTotalValue = () => {
    let totalValue = 0

    for(let product of this.props.productsInCart) {
      totalValue += product.price * product.quantity
    }

    return totalValue
  }

    return (
      <ShoppingCartContainer>
        <h3>Carrinho:</h3>
        <CartListContainer>
          {this.props.productsInCart.map((product) => {
            return <Items 
                      cartItem={product} 
                      onRemoveProductFromCart={this.props.onRemoveProductFromCart}
                    />
          })}
        </CartListContainer>
        <p>Valor total: R${this.getTotalValue()},00</p>
      </ShoppingCartContainer>
    )
  }
