import React, { useState, useEffect } from 'react';
import { createGlobalStyle } from 'styled-components';
import Header from './Components/Header/Header';
import Filters from './Components/Filters/Filters';
import Home from './Components/Home/Home';
import Cart from './Components/Cart/Cart';
import Footer from './Components/Footer/Footer';

import productsData from './data/products';

const GlobalStyle = createGlobalStyle`
  // Estilos globais aqui
`;

export default function App() {
  const [products] = useState(productsData);
  const [minFilter, setMinFilter] = useState('');
  const [maxFilter, setMaxFilter] = useState('');
  const [searchFilter, setSearchFilter] = useState('');
  const [cart, setCart] = useState([]);
  const [amount, setAmount] = useState(0);
  const [checkoutClicked, setCheckoutClicked] = useState(false);

  const handleAddToCart = (productId) => {
    const updatedCart = cart.map((product) => {
      if (productId === product.id) {
        return {
          ...product,
          quantity: product.quantity ? product.quantity + 1 : 1,
        };
      }
      return product;
    });

    setCart(updatedCart);
    setAmount(amount + 1);
  };

  const handleRemoveFromCart = (productId) => {
    const updatedCart = cart.map((product) => {
      if (productId === product.id && product.quantity) {
        return {
          ...product,
          quantity: product.quantity - 1,
        };
      }
      return product;
    });

    setCart(updatedCart);
    setAmount(amount - 1);
  };

  const saveCartToLocalStorage = (cart) => {
    localStorage.setItem('cart', JSON.stringify(cart));
  };

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem('cart'));
    console.log('Saved Cart:', savedCart);
    if (savedCart) {
      setTimeout(() => {
        setCart(savedCart.cart);
        setAmount(savedCart.amount);
      }, 0);
    }
  }, []);

  useEffect(() => {
    saveCartToLocalStorage({ cart, amount });
    console.log('Cart Saved:', { cart, amount });
  }, [cart, amount]);

  const handleCheckoutClick = () => {
    if (cart.length > 0) {
      setCheckoutClicked(true);
    }
  };

  const handleAlertClose = () => {
    setCheckoutClicked(false);
  };

  return (
    <div>
      <GlobalStyle />
      <Header />
      <Filters
        minFilter={minFilter}
        maxFilter={maxFilter}
        searchFilter={searchFilter}
        setMinFilter={setMinFilter}
        setMaxFilter={setMaxFilter}
        setSearchFilter={setSearchFilter}
      />
      <Home
        products={products}
        minFilter={minFilter}
        maxFilter={maxFilter}
        nameFilter={searchFilter}
        onAddProductToCart={handleAddToCart}
      />
      <Cart
        cart={cart}
        amount={amount}
        onRemoveProductFromCart={handleRemoveFromCart}
        onCheckoutClick={handleCheckoutClick}
      />
      {checkoutClicked && (
        <div>{window.alert('A Outer Space agradece a sua compra!')}</div>
      )}
      <Footer />
    </div>
  );
}
