import styled from 'styled-components';


export const FooterContainer = styled.div`
grid-row-start: 3;
display:flex;
flex-direction: row;
justify-content: space-evenly;
grid-row-gap: 10px;
padding: 15px;
background-color: aliceblue;

a{
    text-decoration: none;
    color: black;
}

img{
    width: 35px;
}

.historia{
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
    margin-top: 7px;
}

.contato{
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
    margin-top: 7px;
}

.redes-sociais{
    display: flex;
    flex-direction: column;
    align-items: center;
    flex-direction: row;
    gap: 10px;
}
.rocket-container {
    position: absolute; /* Adicionado position: absolute */
    top: 50%; /* Ajuste a posição vertical conforme necessário */
    right: 20px; /* Ajuste a posição horizontal conforme necessário */
    transform: translate(0, -50%); /* Para centralizar verticalmente */
  }
`
export const RocketContainer = styled.div`
  margin-right: 20px;
`;
