import React, { useContext } from 'react';
import {
  SidebarContainer,
  SidebarClose,
  CloseText,
  CloseIcon,
  SidebarItems,
  SidebarTextContainer,
  SidebarSubContainer,
  SidebarTotal,
  SidebarTrash
} from './SidebarStyle';
import { CartContext } from './CartContext';

export default function CartSidebar() {
  const { cart, removeFromCart, clearCart } = useContext(CartContext);

  const getTotalValue = () => {
    let totalValue = 0;

    for (let item of cart) {
      totalValue += item.price * item.amount;
    }

    return totalValue;
  };

  const formattedTotal = getTotalValue().toLocaleString('pt-BR', {
    style: 'currency',
    currency: 'BRL',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });

  return (
    <SidebarContainer>
      <SidebarClose>
        <CloseText>Seu Carrinho</CloseText>
        <CloseIcon onClick={clearCart}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <polyline points="3 6 5 6 21 6" />
            <path d="M16 10a4 4 0 0 1-8 0" />
          </svg>
        </CloseIcon>
      </SidebarClose>
      <SidebarItems>
        {cart.length > 0 ? (
          cart.map(item => (
            <SidebarTextContainer key={item.id}>
              <SidebarSubContainer>
                <span>{item.name}</span>
                <span>R$ {item.price}</span>
              </SidebarSubContainer>
              <SidebarSubContainer>
                <span>Quantidade: {item.amount}</span>
                <SidebarTrash onClick={() => removeFromCart(item.id)}>
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <polyline points="3 6 5 6 21 6" />
                    <path d="M16 10a4 4 0 0 1-8 0" />
                  </svg>
                </SidebarTrash>
              </SidebarSubContainer>
            </SidebarTextContainer>
          ))
        ) : (
          <SidebarTextContainer>
            <span>Seu carrinho est√° vazio.</span>
          </SidebarTextContainer>
        )}
      </SidebarItems>
      <SidebarTotal>
        <span>Total:</span>
        <span>{formattedTotal}</span>
      </SidebarTotal>
    </SidebarContainer>
  );
}
