import React from 'react';
import { CardContainer, CardInfo, AddToCartButton } from './ProductCardStyle';  
import products from '../../../assets/productsList';

export default function ProductCard(props) {
  const { product, onAddProductToCart } = props;

  return (
    <CardContainer>
      <img src={product.photo} alt={product.name} />
      <CardInfo>
        <p>{product.name}</p>
        <p>R${product.price},00</p>
        <AddToCartButton onClick={() => onAddProductToCart(product.id)}>
          Adicionar ao carrinho
        </AddToCartButton>
      </CardInfo>
    </CardContainer>
  );
}
