import React from 'react';
import { CardContainer, CardInfo, AddToCartButton } from './ProductCardStyle';  

export default function ProductCard(props) {
  const { product, onAddProductToCart, imageUrl } = props;

  const handleAddToCart = () => {
    if (onAddProductToCart && typeof onAddProductToCart === 'function') {
      onAddProductToCart(product.id);
    }
  };

  return (
    <CardContainer>
      <img src={imageUrl1} alt={product.name} />
      <img src={imageUrl2} alt={product.name} />
      <img src={imageUrl3} alt={product.name} />
      <img src={imageUrl4} alt={product.name} />
      <img src={imageUrl5} alt={product.name} />
      <img src={imageUrl6} alt={product.name} />
      <img src={imageUrl7} alt={product.name} />
      <img src={imageUrl8} alt={product.name} />
      <img src={imageUrl9} alt={product.name} />
      <img src={imageUrl10} alt={product.name} />
      <img src={imageUrl11} alt={product.name} />
      <img src={imageUrl12} alt={product.name} />
      <CardInfo>
        <p>{product.name}</p>
        <p>R${product.price},00</p>
        <AddToCartButton onClick={handleAddToCart}>
          Adicionar ao carrinho
        </AddToCartButton>
      </CardInfo>
    </CardContainer>
  );
}
