import React, { useState } from 'react';
import { ProductsContainer, ProductsHeader, ProductsGrid } from './HomeStyle';
import ProductCard from '../ProductCard/ProductCard';
import tShirtImage1 from '../../img/T-Shirt-I-Need-More-Space.jpg';  

export default function Home(props) {

  const [ordination, setOrdination] = useState('');

  const getFilteredAndOrderedList = () => {
    return props.products
      .filter((product) => (props.maxFilter ? product.price < props.maxFilter : true))
      .filter((product) => (props.minFilter ? product.price > props.minFilter : true))
      .filter((product) => (props.nameFilter ? product.name.includes(props.nameFilter) : true))
      .sort((a, b) => (ordination === 'CRESCENTE' ? a.price - b.price : b.price - a.price));
  };

  const onChangeOrdination = (event) => {
    setOrdination(event.target.value);
  };

  const filteredAndOrderedList = getFilteredAndOrderedList();

  return (
    <ProductsContainer>
      <ProductsHeader>
        <p>Quantidade de produtos: {filteredAndOrderedList.length}</p>
        <label>
          Ordenação:
          <select value={ordination} onChange={onChangeOrdination}>
            <option value={'asc'}>Crescente</option>            
            <option value={'asc'}>Crescente</option>
            <option value={'desc'}>Decrescente</option>
          </select>
        </label>
      </ProductsHeader>
      <ProductsGrid>
        {filteredAndOrderedList.map((product) => {
          return (
            <ProductCard
              product={product}
              onAddProductToCart={props.onAddProductToCart}
              image={tShirtImage1}
            />
          );
        })}
      </ProductsGrid>
    </ProductsContainer>
  );
}
