import React, { useState } from 'react';
import { ShoppingCartContainer, CartListContainer, CartTitle, CartTotal, CartIcon, CartCheckout, Message } from './CartStyle';
import Items from '../Items/Items';
import asteroidsImage from '../../img/asteroids.jpg';
import cartIconImage from '../../img/cart.png';
import cartCheckoutImage from '../../img/cart-checkout.png';

export default function Cart(props) {
  const { cart, onRemoveProductFromCart } = props;
  const [showMessage, setShowMessage] = useState(false);

  const sortedCart = cart.slice().sort((a, b) => {
    if (a.name < b.name) return -1;
    if (a.name > b.name) return 1;
    return 0;
  });

  const getTotalValue = () => {
    let totalValue = 0;

    for (let product of sortedCart) {
      if (product.value && product.quantity) {
        totalValue += product.value * product.quantity;
      }
    }

    return totalValue;
  };

  const formattedTotal = getTotalValue().toLocaleString('pt-BR', {
    style: 'currency',
    currency: 'BRL',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });

  const handleCheckoutClick = () => {
    setShowMessage(true);
  };

  return (
    <ShoppingCartContainer style={{ backgroundImage: `url(${asteroidsImage})` }}>
      <CartTitle>
        <h2>
          Carrinho
          <CartIcon src={cartIconImage} alt="Cart Icon" />
        </h2>
      </CartTitle>
      <CartListContainer>
        {sortedCart.map((product) => (
          <Items
            key={product.id}
            cartItem={product}
            onRemoveProductFromCart={onRemoveProductFromCart}
          />
        ))}
      </CartListContainer>
      <CartTotal>
        <h3>
          Total: {formattedTotal}
        </h3>
        <CartCheckout src={cartCheckoutImage} alt='Cart Checkout' onClick={handleCheckoutClick} />
      </CartTotal>
      {showMessage && <Message>Obrigado por comprar na Outer Space!</Message>}
    </ShoppingCartContainer>
  );
}
