import React from 'react'
import Items from '../Items/Items'
import { ShoppingCartContainer, CartListContainer } from './CartStyle'

export default function Cart(props) {
  const getTotalValue = () => {
    let totalValue = 0

    for(let product of props.productsInCart) {
      totalValue += product.price * product.quantity
    }

    return totalValue
  }

  return (
    <ShoppingCartContainer>
      <h3>Carrinho:</h3>
      <CartListContainer>
        {props.productsInCart.map((product) => {
          return <Items cartItem={product} onRemoveProductFromCart={props.onRemoveProductFromCart}/>
        })}
      </CartListContainer>
      <p>Valor total: R${getTotalValue()},00</p>
    </ShoppingCartContainer>
  )
}
