import React, { useState, useEffect } from 'react';
import { TopImageContainer, SiteTitle } from './GlobalStyle';
import Filters from './Components/Filters/Filters';
import Home from './Components/ProductList/Home/Home';
import Cart from './Components/ShoppingCart/Cart/Cart';
import wallpaperImage from './Components/img/Wallpaper.jpg';
import products from './assets/productsList.jsx';
import Footer from './Components/Footer/Footer';


export default function App() {
  const [minFilter, setMinFilter] = useState('');
  const [maxFilter, setMaxFilter] = useState('');
  const [searchFilter, setSearchFilter] = useState('');
  const [cart, setCart] = useState([]);
  const [amount, setAmount] = useState(0);

  const handleAddToCart = (productId) => {
    const productInCartIndex = cart.findIndex((product) => productId === product.id);

    if (productInCartIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productInCartIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      const productToAdd = products.find((product) => productId === product.id);
      setCart([...cart, { ...productToAdd, quantity: 1 }]);
    }

    setAmount(amount + 1);
  };

  const handleRemoveFromCart = (productId) => {
    const productInCartIndex = cart.findIndex((product) => productId === product.id);

    if (productInCartIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productInCartIndex].quantity -= 1;
      if (updatedCart[productInCartIndex].quantity === 0) {
        updatedCart.splice(productInCartIndex, 1);
      }
      setCart(updatedCart);
      setAmount(amount - 1);
    }
  };


    // 1. Atualizar o carrinho somente na primeira inicialização da aplicação
    useEffect(() => {
      const cartData = localStorage.getItem('cart');

      if (cartData) {
        const parsedCart = JSON.parse(cartData);
        setCart(parsedCart.cart);
        setAmount(parsedCart.amount);
      }
    }, []);

    // 2. Atualizar o localStorage sempre que houver mudanças no carrinho
    useEffect(() => {
      const dataToStore = { cart: cart, amount: amount };
      localStorage.setItem('cart', JSON.stringify(dataToStore));
    }, [cart, amount]);


  return (
    <div>
      <TopImageContainer style={{ backgroundImage: `url(${wallpaperImage})` }} />
      <SiteTitle>Camisetas Outer Space</SiteTitle>
      <Filters
        minFilter={minFilter}
        maxFilter={maxFilter}
        searchFilter={searchFilter}
        setMinFilter={setMinFilter}
        setMaxFilter={setMaxFilter}
        setSearchFilter={setSearchFilter}
      />
      <Home
        products={products}
        minFilter={minFilter}
        maxFilter={maxFilter}
        nameFilter={searchFilter}
        onAddProductToCart={handleAddToCart}
      />
      <Cart
        cart={cart}
        amount={amount}
        onRemoveProductFromCart={handleRemoveFromCart}
      />
      <Footer />
    </div>
  );
}
