import styled from 'styled-components';

export const TopImageContainer = styled.div`
  position: absolute;
  width: 100%;
  height: 200px;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
`;

export const SiteTitleContainer = styled.div`
  grid-area: 1 / 1; /* Posiciona o título no canto superior esquerdo */
  justify-self: end; /* Alinha o título à direita */
  align-self: start; /* Alinha o título no topo */
  padding: 20px; /* Adiciona um espaçamento interno ao redor do título */
`;
