import React from 'react';
import { FiltersContainer, InputContainer } from './FiltersStyle';

export default function Filters(props) {
  const {
    minFilter,
    setMinFilter,
    maxFilter,
    setMaxFilter,
    searchFilter,
    setSearchFilter,
  } = props;

  console.log('minFilter:', minFilter);
  console.log('maxFilter:', maxFilter);
  console.log('searchFilter:', searchFilter);

  const handleMinFilterChange = (event) => {
    const value = event.target.value;
    const filteredValue = value.replace(/[^0-9]/g, ''); // Remove caracteres não numéricos

    if (filteredValue === '' || filteredValue === '0' || filteredValue[0] !== '0') {
      setMinFilter(filteredValue);
    }
  };

  const handleMaxFilterChange = (event) => {
    const value = event.target.value;
    const filteredValue = value.replace(/[^0-9]/g, ''); // Remove caracteres não numéricos

    if (filteredValue === '' || filteredValue === '0' || filteredValue[0] !== '0') {
      setMaxFilter(filteredValue);
    }
  };


  const handleSearchFilterChange = (event) => {
    setSearchFilter(event.target.value);
  };

  return (
    <FiltersContainer>
      <InputContainer>
        Preço Mínimo:
        <input
          type="number"
          value={minFilter}
          onChange={handleMinFilterChange}
        />
      </InputContainer>
      <InputContainer>
        Preço Máximo:
        <input
          type="number"
          value={maxFilter}
          onChange={handleMaxFilterChange}
        />
      </InputContainer>
      <InputContainer>
        Nome do Produto:
        <input
          type="text"
          value={searchFilter}
          onChange={handleSearchFilterChange}
        />
      </InputContainer>
    </FiltersContainer>
  );
}
