import React from 'react';
import Items from '../Items/Items';
import { ShoppingCartContainer, CartListContainer } from './GlobalStyle/ShoppingCartContainer';

export default function Cart(props) {
  const { cart, onRemoveProductFromCart } = props;

  const getTotalValue = () => {
    let totalValue = 0;

    for (let product of (props.productsInCart || [])) {
      if (product.price && product.quantity) {
        totalValue += product.price * product.quantity;
      }
    }

    return totalValue.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    });
  };

  return (
    <ShoppingCartContainer>
      <h3>Carrinho:</h3>
      <CartListContainer>
        {cart.map((product) => (
          <Items
            key={product.id}
            cartItem={product}
            onRemoveProductFromCart={onRemoveProductFromCart}
          />
        ))}
      </CartListContainer>
      <p>Total: {getTotalValue()}</p>
    </ShoppingCartContainer>
  );
}
