import React from 'react';
import { ProductsContainer, ProductsHeader, ProductsGrid } from './HomeStyle';
import ProductCard from '../ProductCard/ProductCard';

export default function Home(props) {


  const filteredAndOrderedList = getFilteredAndOrderedList(); // A função getFilteredAndOrderedList precisa ser definida

  return (
    <ProductsContainer>
      <ProductsHeader>
        <p>Quantidade de produtos: {filteredAndOrderedList.length}</p>
        <label>
          Ordenação:
          <select value={props.sort} onChange={props.onChangeSort}>
            <option value={'CRESCENTE'}>Crescente</option>
            <option value={'DECRESCENTE'}>Decrescente</option>
          </select>
        </label>
      </ProductsHeader>
      <ProductsGrid>
        {filteredAndOrderedList.map((product) => {
          return (
            <ProductCard
              product={product}
              onAddProductToCart={props.onAddProductToCart}
            />
          );
        })}
      </ProductsGrid>
    </ProductsContainer>
  );
}
