import React from 'react';
import { ItemsContainer } from './ItemsStyle';

export default function Items(props) {
  const { cartItem, onRemoveProductFromCart } = props;

  return (
    <ItemsContainer>
      <p>{cartItem.quantity}x</p>
      <p>{cartItem.name}</p>
      <p>Valor unitário: R${cartItem.value.toFixed(2)}</p> {/* Alteração aqui */}
      <button onClick={() => onRemoveProductFromCart(cartItem.id)}>Remover</button>
    </ItemsContainer>
  );
}
