import React, { useState } from 'react';
import { ProductsContainer, ProductsHeader, ProductsGrid } from './HomeStyle';
import ProductCard from '../ProductCard/ProductCard';
import tShirtImage1 from '../../img/T-Shirt1.jpg';
import tShirtImage2 from '../../img/T-Shirt2.jpg';
import tShirtImage3 from '../../img/T-Shirt3.jpg';
import tShirtImage4 from '../../img/T-Shirt4.jpg';
import tShirtImage5 from '../../img/T-Shirt5.jpg';
import tShirtImage6 from '../../img/T-Shirt6.jpg';
import tShirtImage7 from '../../img/T-Shirt7.jpg';
import tShirtImage8 from '../../img/T-Shirt8.jpg';
import tShirtImage9 from '../../img/T-Shirt9.jpg';
import tShirtImage10 from '../../img/T-Shirt10.jpg';
import tShirtImage11 from '../../img/T-Shirt11.jpg';
import tShirtImage12 from '../../img/T-Shirt12.jpg';
import productsList from '../../../assets/productsList';

export default function Home(props) {
  const [ordination, setOrdination] = useState('');

  const applyFiltersAndSort = () => {
    const { maxFilter, minFilter, nameFilter } = props;

    // Validando e convertendo os valores para números
    const maxFilterValue = parseFloat(maxFilter);
    const minFilterValue = parseFloat(minFilter);

    // Verificando se os valores são válidos e não negativos
    const isMaxFilterValid = !isNaN(maxFilterValue) && maxFilterValue >= 0;
    const isMinFilterValid = !isNaN(minFilterValue) && minFilterValue >= 0;

    if (!isMaxFilterValid || !isMinFilterValid) {
      return []
    }

    return productsList.filter((product) => {
      // Verificando se o valor máximo é válido e não ultrapassa o limite
      if (isMaxFilterValid && maxFilterValue <= parseFloat(product.value)) {
        return false;
      }
      // Verificando se o valor mínimo é válido e não ultrapassa o limite
      if (isMinFilterValid && minFilterValue >= parseFloat(product.value)) {
        return false;
      }
      // Aplicando o filtro pelo nome (se houver)
      if (nameFilter && !product.name.toLowerCase().includes(nameFilter.toLowerCase())) {
        return false;
      }
      return true;
    }).sort((a, b) => {
      if (ordination === 'asc') {
        return parseFloat(a.value) - parseFloat(b.value);
      } else if (ordination === 'desc') {
        return parseFloat(b.value) - parseFloat(a.value);
      } else {
        return 0;
      }
    });
  };

  const onChangeOrdination = (event) => {
    setOrdination(event.target.value);
  };

  const filteredAndOrderedList = applyFiltersAndSort();

  const productImages = {
    '1': tShirtImage1,
    '2': tShirtImage2,
    '3': tShirtImage3,
    '4': tShirtImage4,
    '5': tShirtImage5,
    '6': tShirtImage6,
    '7': tShirtImage7,
    '8': tShirtImage8,
    '9': tShirtImage9,
    '10': tShirtImage10,
    '11': tShirtImage11,
    '12': tShirtImage12,
  };

  return (
    <ProductsContainer>
      <ProductsHeader>
        <p>Quantidade de produtos: {filteredAndOrderedList.length}</p>
        <label>
          Ordenação:
          <select value={ordination} onChange={onChangeOrdination}>
            <option value={''}>Ordenar</option>
            <option value={'asc'}>Menor Preço</option>
            <option value={'desc'}>Maior Preço</option>
          </select>
        </label>
      </ProductsHeader>
      <ProductsGrid>
        {filteredAndOrderedList.map((product) => {
          const imagesUrl = productImages[product.id];
          return (
            <ProductCard
              key={product.id}
              product={product}
              onAddProductToCart={props.onAddProductToCart}
              imagesUrl={imagesUrl}
            />
          );
        })}
      </ProductsGrid>
    </ProductsContainer>
  );
}
