import React, { useState, useEffect } from 'react';
import { createGlobalStyle } from 'styled-components';
import Header from './Components/Header/Header';
import Filters from './Components/Filters/Filters';
import Home from './Components/ProductList/Home/Home';
import Cart from './Components/ShoppingCart/Cart/Cart';
import Footer from './Components/Footer/Footer';
import products from './assets/productsList.jsx';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
`;

export default function App() {
  const [minFilter, setMinFilter] = useState('');
  const [maxFilter, setMaxFilter] = useState('');
  const [searchFilter, setSearchFilter] = useState('');
  const [cart, setCart] = useState([]);
  const [amount, setAmount] = useState(0);
  const [showAlert, setShowAlert] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);

  const handleAddToCart = (productId) => {
    const productInCartIndex = cart.findIndex((product) => productId === product.id);

    if (productInCartIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productInCartIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      const productToAdd = products.find((product) => productId === product.id);
      setCart([...cart, { ...productToAdd, quantity: 1 }]);
    }

    setAmount(amount + 1);
    setIsUpdating(false); // Reset isUpdating to false when adding items to the cart
  };

  const handleRemoveFromCart = (productId) => {
    const productInCartIndex = cart.findIndex((product) => productId === product.id);

    if (productInCartIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productInCartIndex].quantity -= 1;
      if (updatedCart[productInCartIndex].quantity === 0) {
        updatedCart.splice(productInCartIndex, 1);
      }
      setCart(updatedCart);
      setAmount(amount - 1);
    }
  };

  const handleAlertClose = () => {
    setShowAlert(false);
  };

  const handleCheckoutAlert = () => {
    if (cart.length > 0) {
      setCart([]);
      setAmount(0);
      setShowAlert(true);
    }
  };

  useEffect(() => {
    if (showAlert) {
      window.alert('A Outer Space agradece a sua compra!');
      setShowAlert(false);
    }
  }, [showAlert]);

  useEffect(() => {
    const cartData = localStorage.getItem('cart');
    if (cartData) {
      setCart(JSON.parse(cartData));
    }
  }, []);

  useEffect(() => {
    console.log('Loading cart data from localStorage...');
    const storedCart = localStorage.getItem('cart');

    if (storedCart) {
      console.log('Cart data found:', storedCart);
      setCart(JSON.parse(storedCart));
    } else {
      console.log('No cart data found.');
    }

    if (!isUpdating) {
      const updatedCart = JSON.stringify(cart);
      localStorage.setItem('cart', updatedCart);
    }
  }, [cart, isUpdating]);

  const handlePageUpdate = () => {
    setIsUpdating(true);
    // Additional actions for page update if needed
  };

  return (
    <div>
      <GlobalStyle />
      <Header />
      <Filters
        minFilter={minFilter}
        maxFilter={maxFilter}
        searchFilter={searchFilter}
        setMinFilter={setMinFilter}
        setMaxFilter={setMaxFilter}
        setSearchFilter={setSearchFilter}
      />
      <Home
        products={products}
        minFilter={minFilter}
        maxFilter={maxFilter}
        nameFilter={searchFilter}
        onAddProductToCart={handleAddToCart}
      />
      <Cart
        cart={cart}
        amount={amount}
        onRemoveProductFromCart={handleRemoveFromCart}
        onCheckoutClick={handleCheckoutAlert}
        showAlert={showAlert}
        onCloseAlert={handleAlertClose}
      />
      <Footer />
    </div>
  );
}
