import React from 'react';
import Items from '../Items/Items';
import { ShoppingCartContainer, CartListContainer } from './CartStyle';

export default function Cart(props) {
  const getTotalValue = () => {
    let totalValue = 0;

    for (let product of (props.productsInCart || [])) {
      totalValue += product.price * product.quantity;
    }

    return totalValue.toFixed(2); // Arredonda o valor para duas casas decimais
  };

  const productList = props.productsInCart || [];

  const handleRemoveProductFromCart = (productId) => {
    props.onRemoveProductFromCart(productId);
  };

  return (
    <ShoppingCartContainer>
      <h3>Carrinho:</h3>
      <CartListContainer>
        {productList.map((product) => {
          return (
            <Items
              key={product.id}
              cartItem={product}
              onRemoveProductFromCart={handleRemoveProductFromCart}
            />
          );
        })}
      </CartListContainer>
      <p>Valor total: R$ {getTotalValue()}</p>
    </ShoppingCartContainer>
  );
}
