import React, { useState, useEffect } from 'react';
import { createGlobalStyle } from 'styled-components';
import Header from './Components/Header/Header';
import Filters from './Components/Filters/Filters';
import Home from './Components/ProductList/Home/Home';
import CartIcon from './Components/CartIcon/CartIcon';
import Footer from './Components/Footer/Footer';
import products from './assets/productsList.jsx';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
`;

export default function App() {
  const [minFilter, setMinFilter] = useState('');
  const [maxFilter, setMaxFilter] = useState('');
  const [searchFilter, setSearchFilter] = useState('');
  const [cart, setCart] = useState([]);
  const [amount, setAmount] = useState(0);
  const [isCartOpen, setIsCartOpen] = useState(false);

  const handleAddToCart = (productId) => {
    const productInCartIndex = cart.findIndex((product) => productId === product.id);

    if (productInCartIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productInCartIndex].quantity += 1;
      setCart(updatedCart);
    } else {
      const productToAdd = products.find((product) => productId === product.id);
      setCart([...cart, { ...productToAdd, quantity: 1 }]);
    }

    setAmount(amount + 1);
    setIsCartOpen(true);
  };

  const handleRemoveFromCart = (productId) => {
    const productInCartIndex = cart.findIndex((product) => productId === product.id);

    if (productInCartIndex !== -1) {
      const updatedCart = [...cart];
      updatedCart[productInCartIndex].quantity -= 1;
      if (updatedCart[productInCartIndex].quantity === 0) {
        updatedCart.splice(productInCartIndex, 1);
      }
      setCart(updatedCart);
      setAmount(amount - 1);
    }
  };

  const saveCartToLocalStorage = (cart) => {
    localStorage.setItem('cart', JSON.stringify(cart));
  };

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem('cart'));
    console.log('Saved Cart:', savedCart);
    if (savedCart) {
      setTimeout(() => {
        setCart(savedCart.cart);
        setAmount(savedCart.amount);
      }, 0);
    }
  }, []);

  useEffect(() => {
    saveCartToLocalStorage({ cart, amount });
    console.log('Cart Saved:', { cart, amount });
  }, [cart, amount]);

  return (
    <CartProvider>
      <SidebarProvider>
        <div>
          <GlobalStyle />
          <Header />
          <Filters
            minFilter={minFilter}
            maxFilter={maxFilter}
            searchFilter={searchFilter}
            setMinFilter={setMinFilter}
            setMaxFilter={setMaxFilter}
            setSearchFilter={setSearchFilter}
          />
          <Home
            products={products}
            minFilter={minFilter}
            maxFilter={maxFilter}
            nameFilter={searchFilter}
            onAddProductToCart={handleAddToCart}
          />
          <CartIcon
            amount={amount}
            onClick={() => setIsCartOpen(!isCartOpen)}
          />
          <CartSidebar
            cart={cart}
            isOpen={isCartOpen}
            onRemoveProductFromCart={handleRemoveFromCart}
            onClose={() => setIsCartOpen(false)}
          />
          <Footer />
        </div>
      </SidebarProvider>
    </CartProvider>
  );
}
